CREATE TABLE users (
    user_id serial PRIMARY KEY,
    email varchar(255) NOT NULL,
    password text NOT NULL,
    user_name varchar(255) NOT NULL,
    birth_date date);

CREATE TABLE zodiac_signs (
    sign_id serial PRIMARY KEY,
    sign_name varchar(50) NOT NULL,
    start_month INT,
    end_month INT,
    start_day INT,
    end_day INT,
    strong_side text,
    little_weakness text,
    information text,
    image_path text
);


INSERT INTO zodiac_signs (sign_name, start_month, end_month, start_day, end_day, strong_side,
                          little_weakness, information, image_path)
VALUES
    ('Овен', 3, 4, 21, 19,
     'Овен личность крайне целеустремлённая. Если он действительно чем-то увлечён, он, без преувеличения, готов на всё ради достижения цели. Главное — успеть её достичь до утраты интереса.',
     'Из слабых сторон представителей знака можно отметить непримиримое упрямство, которое часто заходит слишком далеко, нежелание терпеть и ждать, а также неспособность слушать чужие советы, даже если они имеют ценность.',
     'Овен - это первый знак зодиака, представляющий собой символ барана. Он ассоциируется с весной и символизирует новую жизнь. Люди, рожденные под этим знаком, часто обладают сильной волей к жизни и стремятся к переменам.

     Овны известны своей предприимчивостью и любопытством. Они часто бросаются в новые приключения и не боятся рисковать. Их оптимистичный взгляд на жизнь часто вдохновляет окружающих и помогает им добиваться успехов в различных сферах жизни.

     Однако, их импульсивность может привести к нестабильности в отношениях и деловых партнерствах. Они могут быть слишком прямолинейными в своих действиях, что может вызывать конфликты с другими людьми.

     Овны ценят свободу и независимость, поэтому они часто предпочитают работать самостоятельно или в небольших командах. Их энергия и идеи могут быть очень ценным ресурсом для любого бизнеса или проекта.

     В личных отношениях Овны могут быть немного непостоянными из-за своего стремления к новым приключениям. Однако, если они находят правильного партнера, способного поддерживать их энергию и уважать их индивидуальность, они могут быть верными и преданными партнерами.',
     'assets/photo_aries.jpg'
    ),
    ('Телец', 4, 5, 21, 21,
     'Тельцы — личности очень сильные. Они упорны и трудолюбивы, готовы годами много трудиться, чтобы добиться желаемого.',
     'Бывает, что эти прекрасные свойства натуры при определённых обстоятельствах трансформируются в недостатки. И тогда человек становится излишне упрямым, старается добиться того, что на самом деле не нужно, идет к псевдоцелям. Долготерпение людей, родившихся под этим знаком зодиака, тоже может плохо заканчиваться. Накопив недовольство, Телец может сорваться из-за сущего пустяка и устроить грандиозный скандал, не стесняясь посторонних людей, находящихся вокруг.

     Кроме того, Тельцы — отъявленные собственники и редкостные ревнивцы, способные своими подозрениями и сценами изводить даже очень любимых ими людей. Случается с ними и жажда накопительства, которая может перерасти в патологическую жадность.',
     'Образ этого знака Зодиака — бык, что говорит само за себя. Люди, родившиеся под данным созвездием, — труженики, каких еще поискать. Они готовы долго и упорно работать, тянуть самую тяжёлую лямку, чтобы добиться поставленной цели. И по этому упорству их вряд ли кто-то из других представителей зодиакального круга сможет догнать.

     У родившихся под покровительством Тельца характер, несмотря на всё упорство, описанное выше, отличается пассивностью. Да, он готов работать до седьмого и далее пота. Но активно искать каких-то дополнительных возможностей он не станет, а просто продолжит уверенно заниматься своим делом.

     Кроме того, людей этого знака отличает житейская мудрость и практичность. Благодаря чему они становятся хорошими хозяевами, старающимися сделать так, чтобы по максимуму всё шло в дом.

     Именно из-за своей приземлённости Тельцы любят ощущение стабильности и безопасности. Чувство неопределённости и скорых перемен, особенно отдающих пугающей неизвестностью, ими переносится остро и мучительно.

     Типичные представители этого знака Зодиака симпатичны внешне, приятны в общении, легко находят общий язык с людьми. С ними комфортно дружить и просто находиться рядом, например, в рабочем коллективе. Но враждовать не стоит — выведенный из себя Телец напоминает разъярённого быка, который несётся на красную тряпку, сметая всё на своём пути.

     Правда, вывести людей, родившихся под этим знаком, не так уж просто — вне состояния конфликта они крайне спокойны и уравновешенны, а ещё очень терпеливы. Кроме того, большинство Тельцов  от природы имеют повышенный уровень эмпатии.',
     'assets/photo_taurus.jpg'
    ),
    ('Близнецы', 5, 6, 22, 21,
     'Первая выдающаяся особенность Близнецов — это их невероятным образом устроенная психика. Она сочетает в себе одновременно холерический и меланхолический типы темперамента. Отсюда такие частые смены поведения, которые ошибочно принимают за двуличие. Но именно эта особенность делает Близнецов такими интересными и притягивает большое количество людей.

     У Близнецов самая адаптивная психика среди всех двенадцати знаков. Они легче других приспосабливаются к новым обстоятельствам. А умение находить общий язык с людьми заслуживает признания. Близнецы через несколько минут практически безошибочно могут определить, что за человек находится перед ними, как стоит общаться и чего от него ожидать. Они в секунды могут переключаться с одной манеры общения на другую.

     Близнецы обладают неутолимой жаждой к знаниям. Они умеют обрабатывать огромное количество информации. Их знания часто считают поверхностными, но это не совсем так. Если Близнец глубоко не вник в какой-то вопрос, значит он просто не актуален в его жизни на данный момент. Но если понадобится разобраться в какой-то теме, то настолько глубоко и с самых разных сторон его не способен изучить ни один другой знак Зодиака.',
     'Близнецы могут перегружать себя чрезмерным количеством общения, что приводит к периодам, когда они ощущают себя совершенно опустошенными.

     Они настолько торопятся жить, что берутся за несколько дел сразу. Поэтому им может не хватить сил или времени, чтобы сделать качественно и приходится оставлять какую-то часть дел незавершенными.

     Умение быстро переключаться с одной задачи на другую — сильная и слабая сторона Близнеца. Да, оно помогает больше успевать и жить очень интересной жизнью, но сильно перегружает психику и могут возникнуть проблемы со здоровьем. Это влияет на общение с окружающими, потому что именно сфера отношений часто выпадает из зоны внимания Близнецов.

     Близнецы не любят объяснять. Им проще самостоятельно проанализировать и выдать конечный результат. А вот когда их спрашивают, почему они пришли именно к такому выводу, они могут впадать в ступор. Их логические цепочки настолько сложны, что объяснить другим людям порой невозможно.',
     'Близнецы — самый многогранный знак зодиака с рядом выдающихся качеств, которые окружающими не всегда воспринимаются верно. Это очень тонкие, сложные и интересные люди. Их можно познавать всю жизнь и всякий раз открывать что-то новое.

     Пожалуй, один из самых притягательных знаков Зодиака — Близнецы. Характер их легок и интересен. Они логичны, интеллектуальны, коммуникативны, позитивны, легки в жизни и общении, а еще имеют энциклопедический склад ума.

     У них настолько огромный интерес к жизни и познанию этого мира, что Близнецы не могут остановиться на одной дисциплине, всю жизнь чему-то обучаются. Бесконечно меняют хобби и направления деятельности, могут увлекаться несколькими предметами одновременно.

     Близнецы любят много разнообразного общения, которое расширяет их кругозор. У них множество приятелей и знакомых. Но по-настоящему крепко дружат они очень редко. Ведь дружба — это уже глубокое погружение в человека. А это отнимает у Близнецов слишком много энергии и душевных сил. С близкими людьми могут демонстрировать то, чего не показывают всем остальным: душевную нежность и ранимость.',
     'assets/photo_gemini.jpg'
    ),
    ('Рак', 6, 7, 22, 22,
     'У Раков глубокие семейные ценности, поэтому они по праву занимают первое место среди двенадцати знаков зодиака как лучшие семьянины.

     Представители этого знака готовы на самопожертвование ради других людей. Это делает их надежными друзьями и партнерами.

     У Раков великолепная фантазия, поэтому они часто выбирают для себя творческие профессии.

     Этот знак обладает очень сильной интуицией.

     Раки могут хорошо чувствовать переживания других людей. И только от выбора самого Рака зависит, станет это их положительной или отрицательной чертой. Надо признать, что в искусстве манипуляции мало кто может потягаться с Раком. Если он будет использовать эти таланты, чтобы помогать людям, то все будут его любить и ценить. Если же начнет манипулировать людьми, то его начнут ненавидеть.',
     'Раки больше других склонны к эмоциональному выгоранию. В течение всей жизни им приходится прикладывать довольно много усилий, чтобы научиться управлять своими эмоциями.

     Эти люди склонны к самоанализу и самобичеванию. Никто так сильно не сможет раскритиковать Рака, как он сам. Переживаниями он может довести себя до нервного истощения, поэтому важно иметь адекватное окружение, которое вовремя отвлечет и подбодрит его.

     Раки тяжело адаптируются к новым обстоятельствам, они склонны к консерватизму и предсказуемости.

     Бывают излишне доверчивы, от чего особенно сильно страдают в первой половине жизни.',
     'Рака очень легко узнать по манере общения. Если вы слышите, что человек, рассказывая какую-то историю, очень подробно говорит не о самих событиях, а о том, как он чувствовал себя, пока все это происходило, то, скорее всего, перед вами Рак. Характер этого знака очень приятный и душевный, но это нередко прячется под хитиновой панцирной броней. Все дело в том, что представители этого знака очень боятся предательства и боли. И пытаются обезопасить себя любыми доступными способами.

     Раки имеют меланхолический темперамент, который наделяет их высокой сенситивностью, эмоциональной чувствительностью и, как следствие, делает их ранимыми.

     Раки не склонны к открытому противостоянию, стараются сгладить острые углы и не допустить конфликта. Но если прижать к стенке, то будут ожесточенно бороться. Представителям этого знака бывает довольно сложно делиться своими эмоциями и переживаниями с другими людьми. Даже самым близким они могут не раскрывать истинной причины своих волнений. И от этого их довольно сложно понять. При этом Раки больше других знаков зодиака зависят от того, получают они поддержку близких людей или нет. Рак может быть трижды лучшим во всем, но негативное мнение от значимых в его жизни людей может уничтожить его веру в себя. Поэтому главная жизненная задача этого знака — создать вокруг себя поддерживающее окружение.

     Раки потенциально стремятся стать лучшими во всем и стараются не бросать начатое на полпути. Если какие-то обстоятельства заставляют их отступить, то через некоторое время они все-равно возвращаются к намеченным целям и реализуют задуманное.

     Это отличные психологи, но только для других людей. Свои же чувства им довольно сложно осознать, поэтому Раки часто погружаются в самокопания. С возрастом представители этого зодиакального знака становятся более эгоистичными, жестокими и прямолинейными. В случае с Раками это здоровый эгоизм, который помогает им сказать «нет» в тех ситуациях, когда окружающие хотят залезть им на шею.

     Раки любят держаться за прошлое и могут долго хранить обиды. Самыми успешными в жизни становятся те представители знака, которым удается не зачерстветь, отпустить негативные переживания и на основе прожитого опыта создать собственный свод правил.',
     'assets/photo_cancer.jpg'),
    ('Лев', 7, 8, 23, 21,
     'Внутренняя сила и любовь к жизни делает из Льва человека, который никогда не сдается, помогает другим, не забывая о себе. Он открыт приключениям, при этом спокоен и в сложные моменты собран. Знаку зодиака присуща уверенность, решительность, упорство. Львы талантливы, не боятся проявляться — они стремятся к лучшему, но не забывают о простых прелестях жизни. Например, Льву нравятся дети, даже если он не задумывается о родительстве. ',
     'Эгоизм зачастую не дает Льву проявляться в любовных отношениях, доверять партнеру. Представители этого знака зацикливаются на себе и плохо воспринимают критику. Им кажется, что окружающие таким образом хотят их задеть. Иногда поток энергии Льва не дает ему заниматься делами спокойно, никуда не торопясь. Огонь внутри настраивает его на постоянные перемены, реализация которых занимает много времени и сил. Лев злопамятен, не терпит лжи и притворства, ему важно окружать себя только проверенными людьми.',
     'Льву, как знаку зодиака, присуща активная деятельность и стремление к успеху, популярности. Огонь дает ему силу, насыщает и толкает на свершения. Хоть Львы и подвержены влиянию эмоций, это не мешает им добиваться поставленных целей. Амбиции, самообладание, тщеславность — основные характеристики знака зодиака. Но он не так прост, каким кажется на первый взгляд.

     Львы привыкли к размаху — чаще всего они эстеты, которые приходят в восторг от красивых вещей. Им нравятся детали, они подмечают тонкости не только в предметах. Чувствуют людей и знают, как найти подход к каждому человеку. Движущая сила этого знака — стремление к славе. При этом Лев сам по себе щедр, не скупится на слова и эмоции.

     У Львов есть склонность к управлению — это сильные личности, настроенные на завоевание мира. Лев стремится к вершине успеха и достигает ее потому, что знает, чего хочет. Его характер настолько непредсказуем, насколько и силен. Знак невероятно обаятельный, яркий, любящий внимание. Льву важно находиться среди людей и знать их мнение. При этом он четко обозначает свою позицию и не боится показаться странным. Львы очень изобретательны, им нравится узнавать новое и мотивировать близких на свершения. Они не боятся брать на себя ответственность, а еще никогда не останавливаются перед трудностями. Жизненные сложности наоборот закаляют их и делают еще сильнее.

     Внешняя привлекательность, харизма и обаяние делают Льва любимцем публики. Он, как лидер, умеет вести за собой и помогает тем, кто слабее. По его принципам, побеждать надо с помощью упорства и стараний, не прибегая к обходным путям.',
     'assets/photo_leo.jpg'),
    ('Дева', 8, 9, 22, 23,
     'Сила Дев в том, что они редко ошибаются — ведь представители знака умеют просчитывать все свои шаги и избегать неловких ситуаций. Девы уверены, что не стоит тратить время на мечты, важно действовать прямо сейчас. Эта черта приводит их именно к той жизни, где они чувствуют себя максимально комфортно. Еще один отличительный плюс — надежность. Вы всегда можете обратиться за помощью, а Дева не пропадет из виду даже в трудных ситуациях.',
     'Некоторые считают Дев слишком критичными и назойливыми. Они любят совершенство и хотят, чтобы все вокруг тоже стремились к идеалу. Дева легко погружается в поток своих мыслей и зачастую ищет во всем подвох.',
     'Стремление к совершенству делает Деву идеалистом, стремящимся к порядку. Знак принадлежит к стихии Земли — Девы редко витают в облаках и мечтают о том, чего нереально достичь. Своеобразное отношение к труду делает из Девы усердного и ответственного человека, который всегда обращает внимание на детали.

     На первый взгляд Дева может показаться скучной. Представители этого знака часто бывают замкнуты в себе, спокойны. Им не нравится находиться в больших компаниях и проводить время с теми, кого они не знают достаточно хорошо. Преданные и любящие Девы на деле совсем не скучные — их внутренний мир поразит любого, кто захочет поближе узнать такого человека.

     Не бойтесь довериться Деве — представитель знака всегда поддержит и встанет на вашу сторону, если что-то пойдет не так.

     Дева — настоящий реалист. Она смотрит на жизнь так, что та иногда кажется ей немного страшной. Но при этом Дева не боится брать ответственность, умеет управляться с деньгами и знает им цену. Ценности Дев часто не совпадают с ценностями большинства людей. Для них гораздо важнее порядок, стабильность, семья. При этом представитель знака очень чувствителен к энергии окружающих. Хоть разум и берет верх над чувствами, Деве часто необходимо время, чтобы прийти в себя после общения с большим количеством людей.

     Меркурий, который управляет Девой, дает силу для новых свершений. А еще планета наделяет Дев различными умениями и предрасположенностью к творческим начинаниям. Это не значит, что они не могут проявить себя в других сферах. Наоборот, характер Девы помогает ей развиваться в разных сферах и пробовать новое, даже несмотря на сложные обстоятельства в жизни.',
     'assets/photo_virgo.jpg'
