CREATE TABLE moon_phase_advice (
                                   id SERIAL PRIMARY KEY,
                                   moon_phase TEXT NOT NULL,
                                   general_info TEXT NOT NULL,
                                   affairs TEXT,
                                   work TEXT,
                                   home_work TEXT,
                                   money TEXT,
                                   love_relationships TEXT,
                                   communication TEXT,
                                   travel TEXT,
                                   hair_care TEXT,
                                   beauty_self_care TEXT,
                                   health TEXT,
                                   nutrition TEXT
);

INSERT INTO moon_phase_advice
(moon_phase, general_info, affairs, work, home_work, money, love_relationships, communication, travel, hair_care, beauty_self_care, health, nutrition)
VALUES
    ('Новолуние', 'Во время новолуния энергия Луны практически отсутствует. Поэтому настроение, эмоции человека проходят через наименьшую точку активности. В этот период рекомендуется не заниматься ничем важным и копить силы для нового лунного месяца.',
     'Нежелательно браться за новые дела. Любое начинание обречено на неудачу из-за нехватки лунной энергии. Рекомендуется уделить время себе, своим мыслям и планам на ближайшие 30 дней. Текущие неотложные вопросы решать не запрещается, но по возможности лучше перенести их на более активную фазу Луны.',
     'В этот период желательно не работать. Если такой возможности нет, то лучше не занимать активную позицию и не брать на себя много дел. Из-за дезориентации и эмоциональной уязвимости все будет даваться тяжело. Можно выполнять текущие небольшие задачи. Не рекомендуется планировать на новолуние серьезный разговор с начальником, смену места работы.',
     'День не подходит для грандиозных работ по дому: генеральной уборки, начала ремонта. Допускаются мелкие домашние дела. Можно убирать в шкафу, декорировать дом, раскладывать вещи по своим местам. Это освободит пространство для новой положительной энергетики наступающего месяца.',
     'Нежелательно в это время оперировать большими денежными суммами: делать крупные покупки, инвестировать, оформлять кредит, одалживать деньги. Можно заняться текущими расходами, оплатить коммунальные счета или рассчитаться с поставщиком за ранее доставленный товар. При манипуляциях крупными суммами есть риск остаться в убытке или совершить невыгодную покупку.',
     'Новолуние – неоднозначный период для любого общения, в том числе и любовного. Свидание можно назначать только при стабильных отношениях, если нет угрозы ссоры. Ведь энергетика минимальна, поэтому возможны всплески негативных эмоций.',
     'В этот день рекомендуется избегать контактов с людьми. Это позволит накопить энергию для будущего месяца и избежать возможных конфликтных ситуаций. Общаться не стоит не только с незнакомцами, но и с родными и близкими. Если это невозможно, то нужно стараться держать эмоции при себе.',
     'Запланированные заранее поездки на отдых принесут новые впечатления и вдохновят на планирование ближайшего месяца. Служебные командировки не рекомендуются, поскольку рабочие дела будет вести тяжело.',
     'Стрижка в новолуние нежелательна, так как может укоротить жизнь. При окрашивании волос рекомендуется использовать натуральные краски. Это неподходящий день для кардинальных изменений прически. Лучше заплести волосы в косичку или собрать в хвост, чтобы уменьшить приток негативной энергии из космоса.',
     'Новолуние – не лучший период для проведения косметических процедур из-за высокой уязвимости организма.',
     'В этот период нужно следить за самочувствием – возможны вирусные инфекции. Начавшиеся сегодня заболевания будут продолжительными, но не приведут к серьезным последствиям. Нежелательны интенсивные физические нагрузки. В этот день полезно пассивно отдыхать, допускаются неспешные прогулки.',
     'Рекомендуется кушать легкоусвояемые продукты. Не стоит употреблять жирную, жареную еду, мучное, сладкое, консерванты, а также алкоголь. Лучше устроить «разгрузочный» день и дать организму отдохнуть.'
    );

INSERT INTO moon_phase_advice
(moon_phase, general_info, affairs, hair_care, beauty_self_care, health, nutrition)
VALUES
    ('Растущий серп', 'Первая фаза начинается после новолуния и заканчивается, когда диск на небе вырастает до половины. Это пора накопления энергии и роста активности.',
     'В начале цикла растущей Луны энергетика находится на нуле. Поэтому желательно в это время ничего не предпринимать, лучше планировать дела на дальнейший месяц. Спустя день-два можно приступать к выполнению намеченных задач. Денежные вклады и инвестиции в дни первой фазы будут успешными. Это подходящий период для работ по дому. Отличная пора для новых знакомств.',
     'Стрижка приведет к быстрому росту волос, укреплению корней. Это оптимальное время для окраски и тонирования волос в привычный цвет – краска будет держаться хорошо. Экспериментировать с цветом лучше в дни убывающей Луны. Это благоприятная пора для проведения оздоровительных и укрепляющих процедур для волос.',
     'Первая фаза – подходящий период для посещения салона красоты. Уходовые процедуры подарят молодость и здоровье.',
     'В эти дни все лекарственные препараты начинают действовать активнее, поступающие с пищей полезные вещества и витамины усваиваются лучше. Вместе с тем, возможны аллергические реакции, отравления, обострение хронических заболеваний. Это оптимальное время для начала занятий спортом, избавления от вредных привычек.',
     'В этот период организм активно накапливает жиры – это нужно учитывать тем, кто следит за лишним весом.'
    );

INSERT INTO moon_phase_advice
(moon_phase, general_info, affairs, hair_care, health)
VALUES
    ('Первая четверть', 'Во время первой четверти освещена половина Луны. Это период прилива энергии и пополнения сил.',
     'День подходит для активной деятельности, выполнения важных задач и решения сложных вопросов. Можно делать небольшие вложения денежных средств. Из-за высокой эмоциональной неустойчивости в этот период общение с близкими людьми лучше ограничить, чтобы избежать конфликтов.',
     'Первая четверть – нейтральная пора для стрижки, окрашивания волос и экспериментов с прической.',
     'На этот день лучше не планировать оперативное вмешательство в организм из-за медленного процесса заживления ран. Умеренные занятия спортом благоприятно повлияют на здоровье. Это подходящее время для проведения очистительных процедур.'
    );

INSERT INTO moon_phase_advice
(moon_phase, general_info, affairs, hair_care, beauty_self_care, health, nutrition)
VALUES
    ('Растущая луна', 'В течение второй фазы Луна увеличивается и в конце становится полной. Это период активной деятельности.',
     'Эти дни подходят для начала новых дел, выполнения важных и ответственных задач. Заключенные в этот период сделки будут успешными. Это отличная пора для новых знакомств, отдыха в кругу родных и близких людей.',
     'Стрижка на растущей Луне приведет к быстрому росту волос и благоприятно повлияет на их состояние. Краситься лучше в привычный цвет – краска продержится долго. Хороший эффект принесут укрепляющие и питательные маски для волос.',
     'Нежелательно проводить пилинги и косметологические чистки лица – могут остаться рубцы. Депиляция приведет к быстрому росту волос и увеличению их густоты, поэтому ее лучше перенести на убывающую Луну.',
     'В эти дни высока вероятность возникновения острых заболеваний. Нужно беречь себя от стрессов. Можно давать максимальную физическую нагрузку с постепенным снижением интенсивности к полнолунию.',
     'Рекомендуется придерживаться разнообразия, обязательно употреблять каши и тушеные овощи.'
    );

INSERT INTO moon_phase_advice
(moon_phase, general_info, affairs, work, home_work, money, love_relationships, communication, travel, hair_care, beauty_self_care, health, nutrition)
VALUES
    ('Полнолуние', 'Полнолуние – пора наибольшего энергетического подъема, насыщенности всех процессов. Все начинания, отношения и эмоции достигают предела. Нужно быть внимательными, чтобы сильная лунная энергетика не «полилась через край» и не вызвала конфликтов и разрушений.',
     'В этот период можно выполнять небольшие однодневные дела, решать текущие вопросы. Крупные начинания и продолжение серьезных проектов лучше отложить на более благоприятное время.',
     'Полная Луна – идеальная пора для масштабных творческих проектов, ведь в этот период в голову приходит много идей и мыслей. В остальных сферах деятельности лучше взять тайм-аут, допускается лишь решение незначительных вопросов. День подходит для организации рекламных акций и PR-кампаний, так как люди в эту фазу Луны хорошо поддаются влиянию. Планировать серьезный разговор с начальником и менять место работы не рекомендуется.',
     'День подходит для работ по дому. Можно организовать небольшой ремонт или сделать масштабную уборку. Хорошая идея – очистить шкафы от ненужных вещей. Это подарит гармонию в отношениях с семьей и домашний уют. Лучше браться за домашние дела в одиночку, поскольку общение в эту фазу Луны нежелательно.',
     'Не рекомендуется иметь дело с деньгами. Не стоит оформлять кредит, делать покупки, возвращать долги. Особенно нежелательно ввязываться в операции с финансовыми рисками – инвестировать, играть на бирже. Лучше перенести всю деятельность, которая связана с деньгами, на более благоприятный период.',
     'День не подходит для общения и встреч с любимым человеком. Из-за переизбытка энергии люди перестают себя контролировать, что может привести к ссорам. Если отложить свидание невозможно, то нужно держать себя в руках и не давать повода развернуть конфликт. В этот период возрастает вероятность измен, поэтому стоит быть внимательными и не поддаваться минутным эмоциям.',
     'Тяжелый день для любого общения. Рекомендуется избегать контактов с людьми. Не стоит назначать деловые встречи, искать инвесторов и деловых партнеров. Нежелательно общаться даже с родными и близкими. Лучше остаться в одиночестве, уделить время себе и привести в порядок свои мысли и планы на ближайшие дни.',
     'Полная Луна – не лучшая пора для поездок. Не стоит планировать на это время командировку или отпуск из-за высокого риска неприятностей в дороге, краж и травм. Если путешествие уже началось, то рекомендуется прервать его и продолжить поездку в более благоприятный период.',
     'Подходящий день для состригания волос, экспериментов с прической. Стрижка избавит от накопленной негативной энергии и поднимет настроение. Красить волосы лучше в цвет, темнее натурального. Хороший эффект принесут укрепляющие и питательные маски.',
     'В полнолуние повышен риск травматизации. Поэтому не рекомендуется проводить сложные косметологические операции. Эффективными будут процедуры по омоложению и очищению организма.',
     'Могут обостряться нервные болезни и повышаться давление. Поэтому стоит избегать физических и эмоциональных перегрузок. Болезни этого периода пройдут быстро и без последствий. Стоит быть внимательными во время приема лекарственных препаратов и сильнодействующих веществ –  есть вероятность побочных эффектов.',
     'В полную Луну повышается риск отравлений, поэтому стоит кушать только известные продукты и питаться в проверенных местах. Полезными будут очистительные процедуры. Рекомендуется исключить жареную и жирную пищу, алкоголь. Хороший результат даст однодневная диета.'
    );

INSERT INTO moon_phase_advice
(moon_phase, general_info, affairs, hair_care, beauty_self_care, health, nutrition)
VALUES
    ('Убывающая луна', 'Третья фаза начинается после полнолуния и заканчивается в третью четверть, когда на небе видна половины Луны. Это период растраты накопленной энергии и анализа выполнения дел.',
     'Подходящая пора для выполнения сложных задач, анализа проделанной работы. В это время хорошо проводить серьезные деловые встречи, решать важные вопросы. Заключение сделок лучше перенести на растущую Луну. Из-за пересмотра планов и предпочтений в этот период может возникнуть желание изменить направление деятельности. Во время третьей фазы в отношениях усиливается духовная близость, люди проявляют чуткость и внимательность друг к другу. Эти дни подходят для перевода любовных отношений на новый уровень, возобновления разорванных связей, серьезных разговоров и объяснений. Из домашних дел наиболее эффективными будут стирка и влажная уборка.',
     'Стрижка на убывающей Луне сохранит здоровье волос, но замедлит их рост. В эти дни можно экспериментировать с цветом волос – краска смоется быстро.',
     'Третья фаза – благоприятный период для проведения процедур по уходу за телом. Маски, пилинги придадут коже здоровый и красивый вид.',
     'Допускаются интенсивные занятия спортом.',
     'В эти дни пища не откладывается в организме в виде лишней массы тела, поэтому можно кушать все, что хочется, не боясь поправиться.'
    );

INSERT INTO moon_phase_advice
(moon_phase, general_info, affairs, hair_care, health, nutrition)
VALUES
    ('Последняя четверть', 'Во время третьей четверти освещена половина Луны. Это период спада энергии и уменьшения физических сил.',
     'Во время третьей четверти освещена половина Луны. Это период спада энергии и уменьшения физических сил.',
     'Третья четверть – нейтральный день для стрижки, окрашивания и тонирования волос, экспериментов с прической.',
     'На этот день можно планировать оперативное вмешательство в организм благодаря быстрому процессу заживления ран. Умеренные занятия спортом благоприятно повлияют на здоровье. В этот период могут обостриться хронические заболевания, поэтому нужно избегать стрессов, переутомлений.',
     'Можно употреблять любую пищу, но в меру.'
    );

INSERT INTO moon_phase_advice
(moon_phase, general_info, affairs, hair_care, beauty_self_care, health, nutrition)
VALUES
    ('Убывающий серп', 'Четвертая фаза длится до полного исчезновения диска на небе – до новолуния. Это период упадка энергии и жизненных сил, время подведения итогов проделанной работы.',
     'Эти дни подходят для завершения начатых проектов, анализа деятельности и составления новых планов. Заключать сделки, инвестировать денежные средства не рекомендуется – результаты не оправдают ожиданий. В этот период хорошо избавляться от ненужных вещей. Четвертая фаза – время испытаний любви на прочность. Нужно остерегаться ссор и пресекать конфликты. Можно выполнять мелкие работы по дому. Это отличная пора для спокойного отдыха.',
     'Стрижка на убывающей Луне укрепит корни волос, но замедлит их рост. В эти дни хорошо подстригать челку. Можно экспериментировать с цветом волос – краска не продержится долго.',
     'Четвертая фаза – благоприятная пора для посещения салона красоты. Уходовые процедуры помогут сохранить здоровый вид кожи.',
     'Умеренные физические нагрузки благоприятно скажутся на состоянии здоровья. Рекомендуется избегать переутомлений, стрессов.',
     'Ограничений в еде нет – набрать лишние килограммы в эти дни тяжело. Главное – не переедать.'
    );


CREATE TABLE zodiac_lunar_sign_advice (
                                          id SERIAL PRIMARY KEY,
                                          zodiac_sign TEXT NOT NULL,
                                          general_info TEXT,
                                          possibilities TEXT,
                                          prohibitions TEXT,
                                          mood TEXT,
                                          initiatives TEXT,
                                          work_business TEXT,
                                          finances TEXT,
                                          love_family TEXT,
                                          health TEXT,
                                          creativity_education TEXT
);

INSERT INTO zodiac_lunar_sign_advice
(zodiac_sign, general_info, possibilities, prohibitions, mood,initiatives, work_business, finances, love_family, health, creativity_education)
VALUES
    ('Овен', 'Луна в знаке Овна может оказывать значительное влияние на поведение и настроение человека. Она усиливает его духовную и эмоциональную активность, а также влияет на его потребности, желания и отношения. В это время человек может испытывать сильное желание для новых начинаний и изменений в своей жизни. Это хорошее время для улучшения физического здоровья, принятия новых решений или начала новых проектов.',
     'Астрологи утверждают, что Луна в Овне — наиболее благоприятный период для реализации грандиозных планов. Именно сейчас вы сможете сказать, что ваши желания и возможности совпадают. Более того, вы почувствуете, что это действительно так. Можете смело браться за задумки, которые все это время откладывали, переживая за свою некомпетентность или несостоятельность. Более того, вы можете показать отличные результаты даже в незнакомых областях. Изменить свою жизнь к лучшему смогут даже те, кто считал себя неудачником — Луна благосклонна и даст всем возможность почувствовать себя лучше. В этот период все оказываются в одинаковых условиях, но добиться результатов смогут только те, кто не станет сидеть, сложа руки и ждать, пока приключится чудо.',
     'В эти дни нежелательно организовывать шумные застолья. Алкоголь, чересчур сытная пища, незнакомые напитки и яства – всё это может стать опасным. Так же не рекомендуется заниматься экстремальными видами спорта – Луна не против активного отдыха, но против ненужного риска. Торопливость в этот период тоже под запретом – люди становятся активнее, чем обычно, и даже не понимают, куда и зачем они спешат. Это приводит к ошибкам, и вместо одной проблемы получается несколько.',
     'Эмоциональная сфера страдает больше всего, многие испытают всю «прелесть» перепадов настроения. Впечатлительные люди впадают то в апатию, то в истерику, и не могут проконтролировать свое состояние. Но если вы считаете себя уравновешенным и хладнокровным человеком, то максимум, что вам грозит – это беспричинная тревожность. Многие люди ощутят душевный подъем и будут чувствовать желание свернуть горы – здесь кроется подвох, ведь можно просто-напросто не рассчитать силы и свалиться от переутомления.',
     'Период идеален для тех, кто не боится новшеств. Идеи витают в воздухе – остается применить таланты и поделиться результатами труда, главное, чтобы они приносили пользу. Обстоятельства складываются удачно для людей любого статуса и любого возраста. Успеха добьются и умудренные старцы, и вчерашние школьники. Луна не оставит без поддержки ни высокопоставленных господ, ни их подчиненных. Правда, есть опасность заиграться и не заметить «подводных камней» – события развиваются по непонятной схеме, и просчитать все шаги наперед практически невозможно.',
     'Перемены в профессиональной сфере окажутся приятными. Можно использовать это время для поиска подработки или для перехода на более солидную должность. Проще говоря, Луна в Овне – идеальное время для быстрого подъема по карьерной лестнице. Хотя те, кто решился идти по головам, будут разочарованы – Луна не уважает беспринципных карьеристов. Бизнесмены в этот период в шоколаде – можно начинать новые проекты, обзаводиться связями и укреплять отношения с деловыми партнерами.',
     'В эти дни Луна поддержит людей, которых привлекают не сами деньги, а способы, которыми их можно заработать. Действовать лучше без риска – только трезвый расчет, тщательная подготовка ну и немножко азарта, который подтолкнет в нужном направлении. Период можно назвать тренингом денежной грамотности – Луна дает уроки, наказывает за небрежное отношение к финансам и поощряет за умение экономить и планировать траты. Ну а особо одаренных учеников Луна поддержит «премией» – например, выигрышем в лотерею.',
     'Ситуация в сфере личных отношений будет неоднозначной. Можно окружить любимого человека заботой и не дождаться взаимности. А можно быть эгоистом и купаться в комплиментах и признаниях. Луна в Овне – это период проверки любовного союза на прочность. В эти дни нежелательно действовать под влиянием эмоций – даже легкий конфликт приведет к серьезным разногласиям, люди наговорят друг другу столько неприятных слов, что возобновить отношения будет очень сложно. А лучшим помощником в личных делах станет всем знакомый компромисс – хотя Овен их и не любит.',
     'В этот период следует снизить нагрузки и при малейшем намеке на недомогание принять меры. Нужно чаще вспоминать о золотой середине и не ставить организм в экстремальные условия – никаких излишеств быть не должно. В эти дни можно не просто всерьез задуматься о борьбе с вредными привычками, но и воплотить этот план в жизнь. Луна не дает никаких гарантий и не обещает быстрых результатов, но ваши старания будут оценены по заслугам.',
     'Луна в Овне – время рождения гениев. Именно в этот период мы можем разбудить в себе новые таланты, сумеем произвести хорошее впечатление на спонсоров или покровителей. Но творчество идет рука об руку с учебой – озарение не нахлынет просто так. Поэтому общаемся с людьми искусства, читаем нужную литературу, посещаем культурные места – лунная муза прилетит только к достойным людям. Экзамены, назначенные на этот период, пройдут удачно, но лишь в том случае, если тщательно подготовиться к испытаниям.'
    ),
    ('Телец', 'В астрологии период Луны в знаке Тельца считается временем пассивным, но со знаком «плюс». В этот период не стоит действовать без хорошо продуманного плана. Слишком активная деятельность может привести к переутомлению или эмоциональному истощению. Люди, рожденные во время Луны в Тельце, обычно обладают большими запасами энергии, но им нужно быть осторожными со своей импульсивностью и эмоциональными вспышками.',
     'Астрологи утверждают, что в этот период все дела и планы диктуются исключительно желаниями. И если у вас нет настроения что-то делать, вы можете с чистой совестью залечь дома и предаваться сладостному безделью. Но если запал есть — смело отправляйтесь воплощать идеи, которые давно вынашивали. Помните, время всегда поощряет тех, кто не сидит на месте и не стесняется своих амбиций, ведь тот, кто много хочет, как правило, получает тоже прилично, если только не сидит сложа руки. В эти дни Луна вселяет уверенность и помогает, включив холодный расчет, найти самый короткий и правильный путь к успеху. Обязательно сделайте что-то для красоты своего тела: сходите в спортзал или к косметологу, или просто примите расслабляющую ванну с пенкой и свечами.',
     'В этот период нельзя сорить деньгами, совершать необдуманные и стихийные траты. Нежелательно давать в долг, но при этом можно инвестировать. Противопоказано пить много жидкости, потому что сейчас это приводит к отёкам. Уязвимо горло, поэтому стоит избегать его переохлаждения. Нельзя брать деньги в долг, расточительно относиться к продуктам питания, электроэнергии. Обязательно проследите, чтобы все краны были исправны и из них не капала вода: считается, что так «утекает» финансовое благополучие.',
     'Настроение в этом периоде приподнятое. Появляется спокойствие и уверенность в своём будущем. Вырисовывается чёткий план, который остаётся только осуществить. Окружающие более терпимы друг к другу, менее эмоциональны и импульсивны. Период подходит для общения и знакомств: в это время все кажутся лучше, чем есть, и легко найти друга и даже любовь. Хочется стабильности, постоянства, заботится о ком-то и чувствовать ответную привязанность.',
     'Период отлично подходит, чтобы действовать в выбранном направлении. Медлительный, но упорный Телец напоминает: важно не торопиться и двигаться строго по плану. В это время люди не склонны к опрометчивым поступкам, что помогает сделать новое дело перспективным и многообещающим. Особенно удачны начинания в финансовой сфере, своём бизнесе. Знакомства с новыми людьми тоже удачны: легко найти человека, который мыслит так же.',
     'Работа и бизнес сейчас на подъёме. Самое время показать все свои таланты и окунуться в рабочий процесс. Приходят интересные идеи, не сложно заниматься монотонной и однообразной работой. Можно получить повышение или премию, если не сидеть сложа руки, а бороться за своё место под солнцем. Возможны полезные знакомства, которые помогут развить собственное дело, расшириться и выйти на новый уровень дохода. Словом, этот период даёт много возможностей, которые не стоит упускать.',
     'Успехом обернутся финансовые вложения. Можно решаться на серьёзные сделки, заключение важных контрактов. Не стоит брать или давать в долг. Можно вкладывать финансы в перспективное дело, но стоит сначала разобраться в нюансах и просчитать все риски. Телец не любит поспешность, особенно с деньгами, поэтому период вознаграждает тех, кто не рискует, а действует обдуманно, распоряжаясь финансами с умом.',
     'В этом периоде женщины становятся особенно прекрасны и привлекательны. Телец подчёркивает их лучшие качества, смягчает и придаёт мудрости. Мужчине легко влюбиться в это время и поверить, что он наконец нашёл свой идеал. Именно в этот период чаще всего делают предложения руки и сердца, начинают разговор о зачатии ребёнка, совместном проживании. Чувства становятся более глубокими, нежными, а в семье царят покой и равновесие.',
     'Стоит беречь горло и зубы, в остальном период благоприятен. Можно заниматься спортом, давать телу физические нагрузки. Телец – тот еще гурман, так что вы, скорее всего, испытаете нешуточный соблазн увлечься вредной пищей. Старайтесь не идти на поводу у низменных желаний. Период благоприятно влияет на половые органы, увеличивает либидо. В это время стоит уделить время косметическим и оздоравливающим процедурам.',
     'Период отлично подходит для обучения: Телец способствует усидчивости, усиливает мыслительные процессы. Легко узнавать новое, закреплять материал, но вряд ли получится сделать важное открытие или придумать необычный проект. Время подходит для монотонной, а не творческой работы. Людям искусства приходится сложнее: чувствуется недостаток креативных идей, зато можно совершенствовать мастерство.'
    ),
    ('Близнецы', 'Луна в Близнецах указывает на необходимость взаимодействовать с окружающими и больше общаться с самыми разными людьми. Это время может быть полезным для улучшения отношений с близкими, партнерами или коллегами. При этом оно сулит высокий уровень эмоциональной напряженности и даже конфликтов, особенно если вы интроверт. Однако, если приложить усилия, это период успешных переговоров и решения конфликтов.',
     'Кажется, что в этот период нет ничего невозможного. Кажется, что все цели достижимы, мечты уже почти стали реальностью, да и вообще, сам черт не брат. Но есть одно "но" — это все действительно только кажется. В это время очень легко оказаться в плену иллюзий, выбраться из которого без потерь и разочарований практически невозможно. Из-за вакуума, в который нас будто бы запирает Луна, мы поддаемся странному чувству эйфории и, идя на поводу у эмоций, можем наделать глупейших ошибок, расплачиваться за которые придется долго. Осознать, где правда, а где ложь, будет сложно, как и отличить настоящее от вымышленного и наносного. Правда, не обойдется и без положительных моментов: в этот период люди становятся раскованнее и чаще говорят то, что действительно долгое время хотели сказать. Они перестают обращать внимание на мнение окружающих и отдаются интуиции и чутью, исключая границы и условности.',
     'Воздушный поток, которым щедро делятся Близнецы, может унести куда угодно. Тем более, сами Близнецы не знают слова «нет». Следует быть аккуратней, ведь период богат на запреты и ограничения. Нежелательно надолго оставаться в одиночестве и предаваться воспоминаниям. Находясь в компании, не нужно выяснять отношения – это чревато скандалами, эмоции легко выходят через край. Ну и третий, главный запрет – Луна не рекомендует устраивать грандиозный шопинг и сорить деньгами – запросто можно остаться ни с чем.',
     'Настрой у большинства людей позитивный, бодрый, энергия переполняет нас. Здесь кроется опасность – мы можем наворотить столько дел, что разгребать придется очень долго. Нужно контролировать эмоции и следить, как наши действия отражаются на других. Несвойственные нам выходки могут смутить окружающих. Но те, кто устал держать себя на коротком поводке, могут уйти в отрыв, ведь сейчас самое идеальное время для расслабления.',
     'В этот период сама Луна повелевает: будьте дерзкими и ждет вас успех. Ну а кто мы такие, чтобы противиться воле светила? Да и Близнецы подзуживают: ну давай, не тяни, действуй. Слушать авантюристов-Близнецов точно не стоит, а вот прислушаться к внутреннему голосу будет очень даже полезно. Астрологи советуют заняться чем-то необычным и незнакомым — есть вероятность достичь успеха, и более того, вам удастся изменить свою жизнь к лучшему. И если раньше вы скромничали, потому что боялись, что кому-то не понравитесь или о вас подумают плохо, то в этот период даже думать об этом забудьте: получится всё и даже больше!',
     'Как можно думать о делах в такой чудесный период? Но подумать всё-таки придется. Ведь именно сейчас мы можем легко получить новую должность, играючи завести полезные связи и без проблем заключить выгодную сделку. Что касается бизнеса, то тут и карты в руки – свое дело реально начать даже без стартового капитала. А вот бизнесменам, у которых есть средства, Луна советует не торопиться – шансы удвоить капитал и разориться примерно одинаковые.',
     'Деньги существуют для того, чтобы их тратить. Эту красивую фразу нужно забыть – в период, пока Луна в Близнецах, лучше подумать о выгодных вложениях. Можно поучиться управлять капиталом у успешных знакомых. А в идеале решение финансовых вопросов желательно отложить. Мы становимся невнимательными, а рассеянность – это лучшая приманка для мошенников. Луна утешит тех, кто успел расстроиться – если нельзя тратить, значит, можно зарабатывать. Источники дохода повсюду, нужно лишь разглядеть.',
     'Если ссора, то надолго, если брак, то навсегда. В период, когда Луна гостит в созвездии Близнецов, нас «накрывает» – люди становятся чувственными и страстными. Ну а вероятность совершения ошибки при таком коктейле будет высокой. А еще мы перестаем думать об обязательствах – эгоизм вряд ли положительно скажется на отношениях. Период можно сравнить с лавиной, события развиваются стремительно. Общение с близкими людьми похоже на общение соседей – вроде всё хорошо, но всё время ждешь подвоха.',
     'Луна веселится с Близнецами, а наши нервные клетки страдают. Объяснение простое – всего слишком много. Много разговоров, мероприятий, контактов, связей, поэтому происходит перегрузка, и даже самый коммуникабельный человек может стать раздражительным. Но есть хороший рецепт от негатива – дыхательные упражнения. Они могут сочетаться с упражнениями по йоге или идти отдельным блоком. Еще в этот период полезны массажные процедуры, прогулки и активный отдых на свежем воздухе.',
     'Вдохновение может напасть в любой момент. В период Луны в Близнецах нужно постоянно носить с собой ручку и листочек или наговаривать умные фразы на диктофон. Тяга к прекрасному может появиться у кого угодно, и шанс познакомиться с музой есть даже у людей, далеких от искусства. Информация в эти дни усваивается легко, но так же легко исчезает – в одно ухо влетает, из другого вылетает. И тем, кто учится, следует вести конспекты и не забывать, что повторенье – основа ученья.'
    ),
    ('Рак', 'Луна в знаке Рака ассоциируется с выраженным влияние эмоций и обострением внутренних переживаний. Люди могут чувствовать себя более эмоционально вовлеченными и гиперчувствительными. Усиливаются все чувства: если любовь, то до гроба, если страх, то до оцепенения. Этот период может быть полезным для рефлексии, анализа своих чувств и эмоций. При правильном подходе можно улучшить внутреннее равновесие и способствовать эмоциональному и восстановлению. Однако этот период также может усугублять эмоциональную уязвимость и вызывать чувство неопределенности.',
     'Астрологи говорят, что этот период идеально подходит для самоанализа и личностного развития. Вы можете ставить цели, определять приоритеты и направления дальнейшего движения по жизни. Стоит отказаться от кардинальных перемен, зато это время закладывает надежный фундамент для будущего успеха. Луна в Раке сулит небольшую передышку для тех, кто занят бизнесом: все дела и важные сделки стоит отложить и взять небольшой тайм-аут, чтобы посмотреть, не упускают ли они что-то действительно важное. В это время также хорошо заняться делами семейными и постараться укрепить взаимоотношения с близкими.',
     'Нельзя поддаваться плохому настроению, слишком требовательно относиться к себе. В это время легко увлечься самокопанием и впасть в депрессию. Стоит контролировать свои эмоции, потому что они могут быть иррациональны и толкать на поступки, о которых потом придётся жалеть. Нельзя перегружаться физически, переедать, злоупотреблять алкоголем. В это время легко отравиться, спровоцировать аллергию.',
     'Настроение – меняется, как дуновение ветерка. То человек любит весь мир и готов жертвовать собой ради других, то закрывается и никого не подпускает к себе. Перепады настроения происходят неожиданно и часто не поддаются объяснению. Достаточно мелочи, чтобы почувствовать себя счастливым, и случайного неосторожного слова, чтобы разочароваться в себе и людях.',
     'Период подходит для начинаний, связанных с семьёй и отношениями. Можно планировать свадьбу, зачатие детей. Дни хороши для поиска нового дома. Можно планировать ремонт и работать над дизайном интерьера. Знакомьтесь с новыми людьми, не бойтесь показывать свои настоящие чувства. Это время – начало новых отношений, поиска верных друзей.',
     'На работе благополучны переходы на другую должность внутри одной компании, но не увольнение. В это время легко пойти на повышение, при этом причиной станет не профессиональные навыки – решающую роль сыграет межличностное общение. Обычная симпатия и доверительное общение в период Луны в Раке – основа всего происходящего в бизнесе и на работе. Так выбирают партнёров, заключаются сделки. Если представитель компании не нравится и вызывает подозрения, то, как бы ни были привлекательны условия, договор заключить не удастся.',
     'Распоряжаясь крупными суммами, нужно быть осторожным. Все решения должны быть обдуманы и взвешены, поддаваться сиюминутным порывам нельзя. Тем не менее, период подходит для покупки недвижимости. Можно инвестировать, открывать новое дело. Особенно удачны вложения в творчество и искусство, а также такие направления, как цветочный, свадебный бизнес.',
     'Период идеален для того, чтобы выстроить внутри семьи отношения, о которых вы мечтаете. Достаточно дать любимым нежности, внимания и заботы, чтобы исчезли конфликты, появилась гибкость, готовность к компромиссам. Время, проведённое с детьми, будет полезным и приятным: именно сейчас закладываются основы их характера. Если отношения сложные, то сейчас их можно «перекроить», сделав доверительнее.',
     'Уязвим желудок, пищеварительная система, грудь, грудная клетка, печень, суставы. Могут чаще обычного давать о себе знать старые травмы, тянуть суставы и ломить кости. Период отлично подходит для лечения зубов, омоложения кожи. Важно не переедать и придерживаться правильного питания. В это время легко набрать лишние килограммы, от которых потом будет трудно избавиться. Психическое и физическое состояние тесно связаны, что приводит к недомоганию при стрессах, ослаблению иммунитета при конфликтах и ссорах.',
     'В этот период повышается чувствительность, связь с внутренним «я» и внешним миром. Проще выражать свои чувства в творчестве и словах. Время отлично подходит для создания шедевров и отдыха при занятии любимым хобби. Обучение благоприятно, если удастся не поддаваться чувствам и не позволять сердцу контролировать разум.'
    ),
    ('Лев', 'Период Луны в Льве ассоциируется с выраженными эмоциональными реакциями, творческим вдохновением и спонтанными поступками. В этот период человек может чувствовать себя уверенно и предприимчиво, готовым принимать риски и следовать своим чувствам. Люди, родившиеся под влиянием Луны во Льве, обладают огромными запасами энергии, харизматичны и экстравагантны. Также этот период может быть привлекателен для различных видов искусства, таких как театр, музыка и танцы, и хорошо подходит для социальных встреч и вечеринок. Но в то же время перенасыщенность эмоциями может привести к необдуманным поступкам',
     'Астрологи говорят, что в этот период шансов будет гораздо больше, чем обычно. Вот только есть один момент: разглядеть их )и уж тем более воспользоваться) смогут далеко не все. Луна считает, что успеха достоин сильнейший, другими словами грандиозных результатов добьются только те, кто не пасует перед трудностями и несмотря ни на что идет вперед. Лучшим примером в это время будут является огненные красавчики Львы — вот уж действительно у кого стоит поучиться. Беритесь за дело с положительным настроем и огоньком, а все неурядицы и мелкие неудачи воспринимайте стойко и с юмором. Мы не можем предугадать всё, а чему суждено быть, как говорится, того не миновать.',
     'Чем больше просишь, тем меньше получишь. Увы, но в эти дни всё будет именно так. Луна будто мстит Львам за то, что им приносят дары на блюдечке. Ну а остальные просто попадают под раздачу. В этот период нельзя хвастаться, ставить себя выше других. Не рекомендуется тесно общаться с малознакомыми людьми – мы будем падки на лесть и легко попадём под влияние манипуляторов. И не нужно критиковать окружающих – любое грубое слово вернется обратно, и хорошо если только словом.',
     'Мы станем веселыми и счастливыми, но лишь бы наше отличное настроение не спугнуло удачу. Но иногда будут случаться «сбои программы» – бодрость сменяется апатией, оптимизм перетекает в депрессию. Но в целом, с точки зрения эмоционального здоровья, период хорош – многие люди оказываются на позитивной волне и радуются жизни без особых на то причин. Ну а тот, кто говорит, что смех без причины это странно, элементарно завидует.',
     'У Львов всё получается само собой. Остальные знаки зодиака ничем не хуже, Луна включит зеленый свет для любых начинаний. Можно пробовать себя в чем-то новом. Но важно понимать – удача будет сопутствовать только в том случае, если это действительно нравится. Нужно посоветоваться с внутренним голосом, отбросить ненужные мысли и не слушать предостережения осторожных знакомых. Начни жизнь с чистого листа – эта фраза вполне подходит для девиза этого периода, и тот, кто не боится рисковать, обязательно достигнет цели.',
     'Бери и делай. Или не делай – у Луны нет четких рекомендаций на этот счет. Если труд в радость, то и работа будет спориться. Ну а если всё надоело, лучше не мучиться и устроить выходной. В бизнесе ситуация схожая – активным людям повезет и при заключении сделок, и при проведении переговоров. Ну а тем, кто ленится и верит, что проблемы разрешатся по щелчку пальцев, Луна посоветует записаться на тренинги под названием «Как стать успешным». Пользы от этих курсов немного, но зато и вреда не будет.',
     'Они, увы, на исходе. И в этом нет ничего странного – Лев обожает тратить и нас заряжает любовью к шопингу. Ну а если расходы превышают доходы, ждать, что мы внезапно разбогатеем, не приходится. Хотя Луна в это время не жадная, и в этот период могут произойти приятные события – выигрыши в лотерею, находки крупных сумм, а то и нежданные наследства. Но рассчитывать на подобное везение смогут лишь единицы. А остальным нужно отправиться на поиски подработки – шансы улучшить денежную ситуацию будут у каждого.',
     'Стрела Купидона может вонзиться в кого угодно независимо от возраста, статуса или пола. Правда, есть опасность увлечься не тем человеком. В этот период большинство людей ходит в розовых очках, и мы не сразу заметим, что объект страсти далек от идеала. Ситуация в сфере семейных взаимоотношений складывается достаточно стабильно. Луна советует чаще бывать дома, уделять время близким и устраивать для своих родных маленькие праздники.',
     'Никаких стрессов, никаких волнений. Если же мы проигнорируем лунные советы, то организм может взбрыкнуть и отомстить – бессонницей и нервными срывами. Нежелательно повышать нагрузки и мучить себя упражнениями – в эти дни особенно страдает спина, а радикулит может схватить даже тех, кто не слышал этого слова. Следует помнить, что излишества всегда вредят. Это касается и спорта, и питания. Ну и, конечно, алкоголя – жестких запретов нет, но во время застолья желательно контролировать количество выпитого спиртного.',
     'Вдохновение штука тонкая и может нахлынуть, когда угодно. Или не нахлынуть – как повезет. Капризная муза может явиться к людям, далеким от творчества, и проигнорировать талантливых ребят. Но не нужно опускать рук – следует продолжать заниматься любимым делом, даже если кажется, что всё идет наперекосяк. Что касается учебы, то здесь всё складывается неплохо – информация усваивается легко, и знания, полученные в этот лунный период, останутся в голове надолго.'
    ),
    ('Дева', 'Период Луны в Деве связан с эмоциональными потребностями и внутренними чувствами. В это время может повышаться чувство угнетенности, неуверенности и ранимости и, одновременно, желание проявлять заботу о близких с другими. Важно быть осторожным в коммуникациях с другими людьми в это время. В целом, период Луны в Деве может быть полезен для осознания и обработки эмоциональных потребностей.',
     'Астрологи рекомендуют не перенапрягаться в этот период. Да, покровитель-Дева не даст вам схватить звезду с неба, но тем, кто не боится приложить усилия и готов изрядно потрудиться, сулит успех. Важно помнить, чем больше усилий вы приложите в этот момент, тем основательнее результаты получатся на выходе. И пусть поначалу кажется, что вы расходуете энергию впустую — ждущий дождётся и успех не заставит себя ждать. Не стоит поддаваться амбициям и слепо им следовать, лучше займитесь будничными делами, помогите родным и семье.',
     'Нежелательно в эти дни делать пластические операции. При этом можно проводить оздоровительные процедуры, заниматься спортом. Придётся забыть об эгоизме и жить больше не для себя, а для других людей: стоит только начать действовать в своих интересах, как будут возникать препятствия, ухудшающие положение. Время требует больше альтруизма. В этот период нельзя голодать, нарушать законы, в том числе и нравственные нормы. Не стоит экспериментировать, пробуя новый крем или другое косметическое средство.',
     'В этот период хочется сосредоточиться на своём деле и меньше отвлекаться на второстепенные дела. Результата добиться легко, если действовать по плану, но вот общение с другими людьми складывается непросто. Возникает недопонимание, начинаются споры из-за мелочей. Важно мыслить объективно и рационально, стараясь проявлять эмпатию и сопереживание к другим людям.',
     'Период отлично подходит для карьерного роста. Можно начать своё дело, затеять переезд или ремонт. Все начинания, которые касаются улучшения условий жизни, проходят легко. Под знаком Девы нельзя бодро начать работу, а потом делать её спустя рукава. Чтобы получить результат, нужно выкладываться полностью на каждом этапе.',
     'Это хороший период для того, чтобы совершенствовать свои профессиональные навыки. Особенно благоприятно время для людей из сферы торговли и СМИ. На подъёме врачи и педагоги: в это время их опыт очень ценен, а они идеально выполняют свои задачи. Можно начинать свой бизнес, вы внимательны к деталям и собраны, что поможет избежать ошибок и быстрее выйти на стабильный доход.',
     'В этот период хорошо делать инвестиции, копить и приумножать свои доходы. Появляются бережливость, расчетливость, рационализм, реже совершаются стихийные необдуманные покупки. Удачным приобретением будут оборудование, инструменты, техника. В долг деньги нужно давать с осторожностью, да и брать чужие средства тоже не стоит.',
     'В это время мало у кого находятся силы на романтику, поэтому новые знакомства построены больше на выгоде и корысти, чем на искреннем интересе. Другое дело – стабильные и семейные отношения. Дома каждый занят своим делом, но при этом все прикладывают усилия, чтобы семья развивалась, увереннее стояла на ногах. Пусть в это время случается меньше романтических семейных вечеров, но зато ценность проведенного месте времени вырастает. Новые знакомства редко приводят к свадьбе, проблемные пары в этот период часто расстаются.',
     'Сейчас нужно быть внимательнее к своему питанию и здоровью. Важно не переедать, не переутомляться: во всём должна быть норма. Можно делать завивку, окрашивать волосы. Отлично проходят сеансы ароматерапии, массажи, спа. Если вас давно беспокоило что-то, но врачи не могли определить причину, то именно в этот период стоит докопаться до истины. Собранность и сосредоточенность специалистов вам на руку: будет назначено действенное лечение и наконец-то установлен диагноз.',
     'В творчестве будет не хватать стихийности, экспериментов, зато можно повышать своё мастерство и доводить до совершенства уже начатые проекты. Обучение в это период продуктивно: легко усвоить новый материал, научиться тому, что с трудом давалось. Если давно мечтали начать водить машину, то сейчас самое время записаться на курсы. Везде, где нужны точность и расчёт, всё получается легче.'
    ),
    ('Весы', 'Период Луны в Весах считается важным для душевного равновесия и компромиссов. Это период отличается общением, коммуникацией и сотрудничеством: люди более склонны искать способы достичь взаимовыгодных сделок и улучшить отношения. Энергия Весов помогает людям разрешить конфликты через диалог и компромисс. Это хорошее время для общения, в том числе романтических отношений. Также это период подходит творчества и креативности. Однако период Луны в Весах также может привести к нерешительности и неопределенности: люди могут долго искать идеальный выход из ситуации, но опасаются принимать окончательное решение. В это время важно искать баланс и не допускать физических и умственных перегрузок.',
     'Астрологи утверждают, что в этот период удача весьма непостоянна. Она будет вести себя как капризная барышня, которая будто бы и улыбается, но в то же время готова оскалить зубки. Порой вам будет казаться, что дела спорятся и все идет по плану, как вдруг одно досадное недоразумение в миг перечеркнут все. И никакие просчеты, прогнозы и детальный анализ происходящего не помогут: просто нужно смириться с тем, что кому-то везет, а кому-то нет. Уделите время творческим процессам и налаживанию контактов, ведь человеческий ресурс бесценен, а хорошие связи — на вес золота. И помните, все, что вы делаете, должно приносить удовольствие. Не стоит забывать, что результат это, конечно, хорошо, но сам процесс тоже должен быть максимально приятным.',
     'Нельзя поддаваться эмоциям, период располагает к нервным срывам. При этом держать все переживания внутри себя тоже нельзя: это скажется на физическом состоянии. Важна золотая середина и поддержка близких в трудные моменты. Не стоит переедать в это время, хотя очень хочется жирной и вредной пищи. Может возникнуть стихийное желание пожить красиво, поэтому стоит трезво оценивать свои финансы и не брать кредит на огромный телевизор или дорогую путёвку в другую страну. Противопоказан алкоголь.',
     'Внутреннее состояние тоже напоминает неустойчивый баланс Весов. Бурная радость и ощущение счастья сменяется такой же глубокой депрессией для людей, подверженных влиянию Луны. Не такие восприимчивые знаки зодиака переносят это время проще: появляется спокойствие, внутренняя уверенность в своих действиях. Люди открыты для общения, рады помочь другим. Период даёт много новых возможностей, которые можно использовать через полезные знакомства.',
     'Период отлично подходит для новых знакомств, расширения круга общения. Можно как встретить свою любовь, так и верного друга или надёжного партнёра. Легко довериться новому человеку, найти общие интересы и цели. Благоприятны начинания, которые делают ваш внутренний мир лучше или приносят пользу обществу. Под покровительством Весов все хорошие начинания становятся успешными и приносят удвоенную пользу, но и плохие приводят к огромным разрушениям.',
     'Многое зависит от сферы деятельности. Чем больше ваша работа или бизнес связаны с творчеством, тем успешнее будут идти дела. Люди хотят праздника, в это время повышаются продажи цветов и милых безделиц, предметам роскоши. Сидят без большого объёма работы юристам и правоохранительным органам: никто не спешит портить своё настроение судебными тяжбами. Люди становятся на удивление благородными, начинают уважать моральные нормы.',
     'Не стоит планировать на это период заключение серьёзных контрактов, совершение крупных покупок. Влияние Весов приводит к тому, что люди слишком доверяют партнёрам, идеализируют ситуацию и не замечают мелочей, которые им потом дорого обойдутся. Велик риск распоряжаться финансами необдуманно, тратить их впустую на сиюминутные желания. Именно в это время повышается спрос на кредиты, многие из которых потом так и остаются невыплаченными.',
     'Время идеально для того, чтобы укрепить внутрисемейные отношения, насладиться обществом любимых людей и детей. Стоит вспомнить о дальних родственниках, с которыми давно не виделись, отправиться в путешествие, навестив старых друзей. Общение с близкими дарит много положительных эмоций, помогает обрести внутреннее равновесие, определить цели и приоритеты. Конфликты возникают редко и решаются легко.',
     'Организм в этот период уязвим. Важно укреплять иммунитет, правильно питаться, вести здоровый образ жизни. Использовать лекарства стоит с осторожностью, только после консультации с врачом. Физическое и душевное состояние сейчас тесно связаны, поэтому лучше исправить причину плохого настроения. Обостряется панкреатит, ухудшается состояние людей с диабетом. Даже небольшие дозы алкоголя приводят к плохому самочувствию, а то и к острому отравлению.',
     'Заниматься творчеством и посвящать себя искусству в этот период приятно и полезно. Можно посещать выставки, развиваться духовно, медитировать. Обучение проходит не так легко: часто личные переживания мешают сосредоточиться и отвлекают от дела. Стоит закреплять навыки, но не браться за слишком сложные проекты.'
    ),
    ('Скорпион', 'Период Луны в Скорпионе связывается с глубинными трансформациями, которые влияют на человека на уровне его душевных и эмоциональных потребностей. В этот период может возникнуть желание проанализировать свою жизнь, выявить запутанные отношения или разобраться со сложными эмоциональными ситуациями. В это время человек может почувствовать необходимость в интроспекции и поиске внутреннего понимания. Также в период Луны в Скорпионе человек может чувствовать себя более энергичным и желающим контролировать ситуацию, чем обычно. Это может привести к усилению черт лидерства и амбициозных планов. Также может наблюдаться повышенный уровень стресса и внутреннего давления.',
     'Астрологи отмечают, что в этот период на поверхность выползают все странности и непонятности. Люди будут вести себя странно, но постарайтесь разглядеть в их поведении положительные моменты. У вас возникнет ощущение, что вы можете покорить весь мир, правда, длиться оно будет недолго, и, скорее всего, окажется ложным, но это не помешает вам ставить цели и добиваться их. Ведь если человек не боится отступить от общепринятых рамок, он способен действовать нестандартно. Единственное, о чем стоит помнить — не стоит заигрываться, ведь эти действия могут завести вас на кривую дорожку, которая со временем рискует превратиться в «наклонную». Помните, Луна в Скорпионе – это время решительных, смелых и сильных людей. Ну а ошибки можно воспринять как опыт, пусть жесткий, но всё-таки необходимый.',
     'Луна и Скорпион объединяют водные стихии, и мы чувствуем себя так, будто стоим посреди океана. А вокруг простор – что же там за горизонтом? А за горизонтом может быть всё что угодно – и чудовища, и сказочные острова. Нельзя поддаваться искушениям и попадать во власть иллюзий. В этот период легко разрушить даже крепкие отношения, поэтому стоит избегать скандалов. В эти дни можно запросто переступить черту и совершить ужасный поступок, поэтому не нужно втягиваться в рискованные авантюры.',
     'Настроение – будет часто меняться. Сегодня мы весельчаки, а завтра пессимисты. Контролировать эмоции в этот период могут единицы, остальным хочется выпустить пар. Главное, не переборщить – можно закупиться небьющейся посудой или приобрести подушки, которые не жалко рвать. И самый важный совет – в моменты приступов гнева лучше быть наедине с собой.',
     'У Скорпиона удивительная память. И в этот период мы тоже можем стать ходячими компьютерами, нырнуть в воспоминания и выудить весь свой прошлый опыт, чтобы использовать его в начинаниях. Луна говорит – можно всё, Скорпион добавляет – но осторожно. Эксперименты будут удачными, и совершенно не важно, в какой области мы будем применять таланты. Здесь главное, не заиграться и не игнорировать подсказки интуиции.',
     'Достичь успеха в профессиональной сфере смогут только дерзкие и смелые люди. А как иначе – Скорпион не бьет баклуши и ждет того же от нас. А Луна в этом вопросе согласна с питомцем Плутона и требует полной отдачи. Так что вкалываем и даже не думаем об отдыхе. Ситуация в бизнесе – период идеален для заключения сделок и поиска новых партнеров. Но если предприниматели не воспользуются удачными условиями, то покатятся назад – к тому, с чего начинали.',
     'Делай деньги, а остальное всё дребедень, напоминает Луна в Скорпионе. С таким подходом многие сумеют и разделаться с долгами, и даже разбогатеть. Но у Луны есть условие – никакого обмана. То есть, если вы задумали перехитрить партнеров или нарушить правила, то вас ждет куча неприятностей – Скорпион справедлив по-своему, но мошенников на своей территории он не потерпит. Что касается трат, то здесь всё просто – сумеешь заработать, сможешь и потратить. А лентяи останутся без сладкого – всё по-честному.',
     'Личная жизнь заиграет яркими красками. Любвеобильный Скорпион не будет скупиться на подарки, и период буквально заваливает нас сюрпризами – наклоняйся, поднимай, пользуйся. На личном фронте везет тем, кто не связан узами брака – заводить новые романы можно хоть каждый день. Но если дома ждут половинка и дети, об измене нельзя даже мечтать – Луна со Скорпионом уже придумали наказание для тех, кто заводит интрижки, забыв об обязательствах (будет не больно, но обидно).',
     'Между настроением и самочувствием сейчас можно смело ставить знак равенства. При позитивном настрое организм будет полон сил и энергии. Ну а если хочется поныть и пожаловаться на жизнь, то можно готовиться к неприятным сюрпризам – тело вспомнит все промахи и отомстит болячками и недомоганиями. Луна советует: даже если всё плохо-плохо, улыбаемся и ищем положительные эмоции везде, где только можно. Тогда в этот период проблемы со здоровьем обойдут стороной.',
     'Люди искусства в этот период на подъеме. Луна дает добро, Скорпион делится идеями. К тому же, нет никаких ограничений – можно воплощать в жизнь любые творческие планы. Правда, в эти дни творцы столкнутся с жесткой конкуренцией – достичь успеха хотят все, а награды для всех не хватит. Процесс обучения проходит неплохо – мы легко воспринимаем новую информацию. А вот ситуация с экзаменами неоднозначная – интеллектуальные испытания выдержит только тот, кто усердно готовился.'
    ),
    ('Стрелец', 'В период Луны в Стрельце человек может испытывать желание действовать или принимать решения быстро и энергично. Это время высокой активности, инициативы и поиска новых возможностей. В этот период трудно удержаться на одном месте или сосредоточиться на деталях. Луна в Стрельце также может привести к высокой эмоциональной чувствительности и нестабильности, так как это знак, связанный с бурными эмоциями и энергией.',
     'Астрологи утверждают, что в этот период нет ничего невозможного. Самые смелые и безумные идеи, самые спонтанные мысли — все имеет место быть и будет непременно реализовано. Покровительство Стрельца сулит успех во всех начинаниях, более того, вы сможете проверить себя на прочность, выяснив, насколько вы действительно хотите добиться поставленных целей, и что готовы для этого сделать. Не бойтесь контактов и общения с незнакомыми людьми, возможно среди них окажется единомышленник, с помощью которого вы сумеете запустить успешный проект. В это время люди готовы помогать бескорыстно, не пряча за пазухой камень и не ожидая ничего взамен, поэтому отбросьте подозрительность и смело двигайтесь вперед. Все, что приходит в вашу жизнь в этот период, нужно принимать с благодарностью и вниманием.',
     'Нужно быть осторожнее с обещаниями и не переоценивать свои силы, потому что это приведёт к разочарованию человека, который вам поверил. Стоит быть осторожнее с острыми предметами и инструментами, потому что повышена опасность получения травм по неосторожности или в спешке. Уязвима печень, желудок, поэтому стоит не переедать в этот период. Следите за своим здоровьем: в погоне за целью может быть нарушен режим сна, увеличены нагрузки на организм, что приведёт к болезням. Рассчитывайте свои силы и не переутомляйтесь.',
     'Один и самых позитивных периодов. Стрелец дарит всем хорошее настроение, моральные силы, воодушевление. Приятно достигать своих целей и помогать другим. Люди сплачиваются, больше нет одиночек, которые держатся особняком. Приходит понимание того, что мы живём в социуме, где каждый важен. Хочется стать лучше, быть примером для подражания, стать значимым для общества и близких.',
     'Удача ждёт в бизнесе, учёбе, путешествиях. Стоит максимально использовать влияние Луны и Стрельца для творческих решений, рискованных проектов. Можно заняться чем-то совершенно новым для вас. Благоприятны начинания, связанные с общением с другими людьми. Можно организовывать сообщества, клубы, партии, разные объединения. Это удачный период для судебного разбирательства.',
     'В этот период можно многого достигнуть при продвижении по карьерной лестнице. Если вам кажется, что вы засиделись и достойны повышения, то самое время намекнуть об этом начальнику. Успех не свалится на голову просто так: нужно проявить упорство, быть уверенным и показать, что чего-то стоите. Бизнес удачен, особенно направления, связанные с точностью, расчётами, общением с людьми. На подъёме адвокатские конторы, повышен спрос на услуги юристов и нотариусов.',
     'Деньги легко приходят и уходят, поэтому нужно вовремя направить их в нужное русло, чтобы они работали на вас, а не просто тратились впустую. Сейчас хочется попробовать много нового, успеть и поработать, и отдохнуть. Стоит соблюдать баланс и не погружаться только в беззаботный отдых, пока не кончатся финансы, или в беспросветную работу, пока проблемы со здоровьем не заставят потратить весь доход.',
     'Стрелец – не самый семейный знак зодиака, но не стоит забывать о его романтичности, склонности идеализировать отношения и партнёра. В этот период любящие пары откроют в своей половинке новые грани и смогут заново влюбиться. Если в отношениях назрела проблема, то влияние Стрельца подтолкнёт к конфликту и даже расставанию. В это время нет полумер: или сильная любовь и восхищение, или разочарование и поиск новой любви.',
     'Повышена активность, но это не всегда идёт на пользу. Человеку кажется, что он может всё вне зависимости от возраста и физического состояния, но неподготовленный организм говорит об обратном. Сейчас легко переоценить свои возможности и получить растяжение, а то и травму посерьезней. Время хорошо подходит для лечения лёгких, восстановления органов дыхания. Полезно делать массаж, принимать лечебные ванны. Период благоприятен для пластических операций, проведения процедур для снижения веса. Сейчас не стоит делать татуировки или прокалывать уши.',
     'Период отлично подходит для творчества. При этом именно коллективные работы заслуживают всеобщего признания и восхищают окружающих. Можно пробовать новые направления искусства, хобби, увлечения. Сейчас самое время для экспериментов и поиска себя. Процесс обучения тоже удачен, во многом благодаря педагогам, которые как никогда воодушевлены своей работой. Самостоятельное изучение даётся сложно, а вот семинары, лекции, консилиумы приносят пользу.'
    ),
    ('Козерог', 'Период, когда Луна находится в Козероге, часто характеризуется повышенной серьезностью и структурированностью. Люди становятся более практичными и целеустремленными, что может способствовать достижению долгосрочных целей. Это время подходит для планирования и реализации серьезных проектов.',
     'Влияние Козерога создает возможности для достижения профессиональных успехов и карьерного роста. Это подходящее время для разработки стратегий, создания планов и анализа текущих дел. Вы можете проявить лидерские качества и взять на себя ответственность за важные проекты. К тому же, в этот период часто открываются новые горизонты для самосовершенствования и личностного роста.',
     'Время Луны в Козероге требует осторожности в эмоциональных реакциях. Излишняя строгость и критичность могут привести к конфликтам, как в личной жизни, так и на работе. Не стоит начинать новые проекты, которые требуют значительных эмоциональных вложений, так как они могут оказаться неуспешными. Также лучше воздержаться от серьезных финансовых решений и сделок, которые могут повлиять на ваше будущее.',
     'Луна в Козероге часто вызывает чувство ответственности и серьезности. Люди становятся более замкнутыми и сосредоточенными на своих целях. В то же время может наблюдаться повышенное чувство тревожности и сомнения в своих силах. Важно находить баланс между работой и отдыхом, чтобы избежать эмоционального выгорания.',
     'Это подходящее время для начала долгосрочных проектов, особенно тех, которые требуют усердной работы и терпения. Все, что требует планирования и систематического подхода, будет иметь хорошие шансы на успех. Однако стоит избегать спонтанных решений и действий, которые могут нарушить тщательно выстроенные планы.',
     'В период Луны в Козероге рекомендуется сосредоточиться на карьерных целях и долгосрочных проектах. Это время хорошо подходит для повышения квалификации, получения новых знаний и навыков. Работодатели могут оценить вашу преданность делу и стремление к развитию. Важно устанавливать четкие границы в рабочих отношениях и избегать ненужных конфликтов.',
     'Финансовые вопросы требуют особого внимания и осторожности. Это время подходит для составления бюджета и планирования расходов. Лучше избегать крупных инвестиций и финансовых рисков, так как они могут оказаться невыгодными. Если вы хотите взять кредит или займ, убедитесь, что вы полностью понимаете условия и способны выполнять обязательства.',
     'В личной жизни в период Луны в Козероге может наблюдаться некоторое напряжение. Отношения требуют серьезного подхода, и может возникнуть необходимость обсуждать важные вопросы. Важно проявлять терпение и внимательность к партнеру, чтобы избежать недопонимания. Время подходит для укрепления существующих связей, но не стоит начинать новых романов, так как они могут не продлиться долго.',
     'Физическое состояние в этот период может быть стабильным, однако стоит обращать внимание на стресс и его влияние на здоровье. Рекомендуется заниматься физической активностью, чтобы поддерживать хорошую форму. Обратите внимание на состояние суставов и костей, особенно если у вас есть предрасположенность к заболеваниям в этих областях.',
     'Период Луны в Козероге не всегда способствует творческим порывам, однако может быть полезным для изучения новых навыков и дисциплин. Обучение должно быть структурированным и целенаправленным, что поможет вам усвоить материал. Творчество может проявляться в практических и конструктивных формах, таких как рукоделие или создание чего-то полезного.'
    ),
    ('Водолей', 'В период Луны в Водолее люди могут испытывать необъяснимую потребность в путешествиях, поиске новых впечатлений и опыта. Это период вдохновения и творчества. Люди чувствуют себя бодрыми и жизнерадостными. Однако необходимо быть осторожным, чтобы не переборщить с эмоциональным вовлечением или экстремальными поступками. В целом, период Луны в Водолее может быть полезен для тех, кто хочет разнообразить свою жизнь и найти новые горизонты.',
     'Больше никаких запретов и рамок — они разрушены и забыты. Все, что было в голове, исчезло, оставив место для смелых идей и грандиозных планов. В этот период легко поверить в себя и с гордо поднятой головой идти к намеченной цели. Единственное, о чем стоит помнить — не переоцените свои возможности! И если кто-то из близких предлагает руку помощи, не отказывайтесь — самоуверенность и тщеславие еще никому не шли на пользу. Ставьте высокие цели, но они не должны быть сильно растянуты во времени — вам нужно все успеть, пока влияние Водолея не ослабло.',
     'Нельзя поддаваться эмоциям и слишком бурно реагировать на критику окружающих. Стоит быть осторожнее в поездках, избегая экстремальных увлечений, так как велик риск травм. Не стоит знакомиться в этот период: романтическое общение будет приятным, но ни к чему не обязывающим, и принесёт разочарование. Нельзя поддаваться порывам и менять место работы, если это не было запланировано ранее.',
     'Водолей усиливает свободолюбие, стремление к духовному росту, желание найти единомышленников. Люди настроены на общение, сотрудничество, с лёгкостью находят общий язык. Но нельзя забывать о соблюдении небольшой дистанции: вторгаться в чужое личное пространство и устанавливать там свои правила точно не стоит. Возможны перепады настроения, может казаться, что вас недостаточно ценят и любят.',
     'Астрологи рекомендуют отложить все текущие дела и уделить время тому, что вам действительно интересно. Любые, даже смелые начинания, обречены на успех, но только в том случае, если вы будете верить в себя и не боятся действовать. Луна любит смелых, поэтому не стоит ее разочаровывать. И да, помните: никто не может повлиять на вас и ваш выбор — каждый человек сам выступает мерилом своих возможностей. Вероятность счастливых случайностей, неординарных историй и судьбоносных встреч в этот период увеличивается, главное не забывать, что чудеса случаются с теми, кто в них верит.',
     'Хорошо открывать новые филиалы, искать партнёров, работать в новом направлении. Самое время перевести бизнес на новый уровень и получать пассивный доход. Все рабочие проблемы решаются быстро и легко, благодаря неординарному подходу коллег и умению совместно находить решение. В то же время с руководителем лучше общаться меньше: его смелые идеи или ваши случайные предложения, придётся воплощать в жизнь вам. Инициатива с начальством сейчас наказуема потерей времени, которое вряд ли будет оплачено.',
     'Можно отдавать долги или брать их. Оформляйте кредиты на свои цели и осуществляйте мечту. Можно купить квартиру или открыть своё дело: вернуть деньги получится раньше обозначенного срока. Сейчас можно рисковать и вкладываться в непростые дела, в которых можно сорвать куш или прогореть. Стоит поучаствовать в лотерее, даже если вы никогда не выигрывали.',
     'Даже в долгих и стабильных отношениях в этот период появляется пикантность и непредсказуемость. Иногда это милые и романтичные моменты, которые запоминаются на всю жизнь, но бывает и что это ссоры без видимых причин, когда в стену летят тарелки и произносится много неприятных слов. Положительные эмоции усиливаются, а отрицательные отравляют отношения, поэтому так важно замечать моменты, когда кто-то в семье расстроен, чтобы вовремя помочь ему восстановить душевное равновесие.',
     'Физическое состояние на подъёме, но стоит избегать стрессов и негативных эмоций. Полезно больше гулять, закаляться, заниматься спортом. Важно полноценно питаться, чтобы организму хватало ресурсов в этот физически и морально насыщенный период. Уязвимы голени и лодыжки: не стоит их слишком перенапрягать, вероятны травмы. Реже беспокоят боли в спине, заболевания сердца и сосудов.',
     'В этот период творчество, хобби и искусство вдохновляет и вызывает удовольствие. Приятно погрузиться в творческий процесс и не ограничивать свою фантазию. Можно придумать что-то совершенно новое и необычное, удивив не только окружающих, но и самого себя. Обучение в группах проходит легко, особенно если это касается совместных проектов. Тяжело даётся рутина и заучивание материала. Занятие должно быть интересным, чтобы легко усваивались знания.'
    ),
    ('Рыбы', 'Период Луны в Рыбах связан с интуитивными чувствами, эмоциями и мечтами. Это время может быть идеальным для креативных проектов и развития внутреннего мира. Люди чувствуют себя более чувствительными и эмоциональными, что может повлиять на их отношения с другими. В это время также может появляться сильное желание покоя и уединения. Реализовать идеи и планы в это время может быть трудно из-за неясности и неопределенности.',
     'Астрологи говорят, что в «лунно-рыбные» дни дарят не только возможности, но и препятствия. Да, возможностей в этот период масса, но вот только и помех хоть отбавляй. Вам все время будет казаться, что весь мир ополчился против вас, и конца этой веренице неудач нет. Но не стоит подозревать вселенную в заговоре — это лишь стечение обстоятельств, на которые порой мы не можем влиять. Сильные справятся с ними и еще больше заматереют, ну а слабые будут хныкать, разводя руками, и винить в неприятностях кого угодно, но только не себя. Луна советует вспомнить о лягушках из сказки – кто побарахтается, тот и выберется из крынки с молоком, взбив масло, ну а кто заляжет на дно — там его и найдут.',
     'В эти дни многие люди склонны к нервным срывам. А Луна не любит, когда мы ссоримся – желательно сдерживать эмоции и не обижать окружающих критикой и замечаниями. Выяснения отношений тоже под запретом – беседы на повышенных тонах ни к чему хорошему не приведут. В этот период нельзя ввязываться в рискованные авантюры и искать опасные развлечения. Адреналина мы получим с избытком, а вот последствия экстремальных забав могут быть очень печальными, и исправлять ошибки придется долго. Также не рекомендуется прикладываться к бутылке в эти дни, и поддаваться соблазну покурить «всего одну» сигарету, если вы в завязке, не стоит: сейчас легко обрасти новыми зависимостями.',
     'Положительные эмоции повсюду – они будто обволакивают, и у нас появляется иллюзия, что так будет всегда. В этом позитивном коконе тепло и сухо, но мы не видим всего того, что происходит вокруг. А улыбаться в одиночку скучно и неинтересно, да и отвыкать от проблем нежелательно – они имеют свойство нападать внезапно. Поэтому радуемся жизни и не забываем иногда снимать розовые очки – мера нужна во всем, даже в счастье.',
     'В этот период вы можете стать особенно деликатными и нерешительными и это легко объяснить: робкие Рыбки не любят совершать резких движений и покидать свою зону комфорта. Стоит не забывать, что Луна не будет ждать, пока мы раскачаемся, и просто пойдет себе дальше, в следующее созвездие, а мы так и будем колебаться, упустив все выгодные возможности. Поэтому астрологи советуют спрятать скромность куда-подальше и ринуться на передовую, ведь только смелым покоряются моря. Смело воплощайте планы в жизнь и не думайте о негативных последствиях — дерзость и даже наглость — вот ваши главные инструменты в этот период. И везение не заставит себя ждать.',
     'Рыбки не любят ответственности и стараются избегать работы, где нужно принимать самостоятельные решения. Но мы должны поступить наоборот – чем чаще мы будем проявлять инициативу, тем больше шансов появится получить новую должность или добиться повышения оклада. И ни в коем случае нельзя стыдиться своей профессии. Можно быть великолепным дворником и дрянным министром – Луна напоминает, что не место красит человека. В бизнесе то же самое – повезет тем, кто честен, а вот дела с криминальным душком приведут к банкротству.',
     'Наконец-то мы дождались периода, когда можно тратить и тратить. Рыбы падки на всё яркое и блестящее и готовы спустить в магазине все средства – многие люди заразятся этой любовью к шопингу. Но ничего – денежки дело наживное, и в эти дни у нас будет время пополнить свою казну и найти новые источники дохода. Однако Луна посоветует иногда включать экономиста – лучше купить что-то одно большое и полезное, чем потратить сбережения на сотню мелких, но ненужных вещиц.',
     'В эти лунные дни и ночи мы будем на одной волне со своими возлюбленными. Хотя все Рыбы немножечко эгоисты, они не против того, чтобы мы посвятили период налаживанию отношений с половинками. Нужно выполнять прихоти партнеров по браку и баловать их – тогда у нас будет шанс вернуться в медовый месяц. Ну а Луна проследит, чтобы мы не забыли и о семейных обязанностях – родственники тоже нуждаются в заботе и будут рады, если мы попытаемся разнообразить жизнь и улучшить атмосферу в доме.',
     'Сопротивляемость болезням повысится, и многие люди могут смело сказать: мы абсолютно здоровы. Да вот только Луна предупреждает: если есть скрытые болячки, в этот период они спрячутся еще глубже. Следует найти время для посещения специалистов и для прохождения медицинского осмотра. Лучше быть здоровым параноиком, трясущимся из-за каждого чиха, чем больным оптимистом, который машет рукой даже на серьезные симптомы – перестраховка в эти дни не навредит, и организм оценит наши усилия.',
     'В этот период можно достичь вершин славы... или хотя бы подняться на ступеньку повыше. Но придётся потрудиться: сидеть и ждать, когда муза к вам прилетит на волшебном ковре, — так себе стратегия. Даже ковёр-самолёт может застрять в пробке! А Рыбы — они как тот выпивший чудак на вечеринке: непонятно, о чем они говорят, но иногда из их слов можно выудить нечто гениальное. Даже если идея в голове звучит как сценарий научно-фантастического фильма — берись за дело!'
    )